import React, { useState, useEffect } from 'react';
import { Card, Divider, Tag, Input } from 'antd';
import { PlusOutlined, PhoneOutlined, EnvironmentOutlined, MailOutlined } from '@ant-design/icons';
import './styles.css';

export default function AccountInfo() {
    const [inputVisible, setInputVisible] = useState(false);
    const [inputValue, setInputValue] = useState('');
    const [newTags, setNewTags] = useState([]);
    let auto = undefined;

    useEffect(() => {
        if (inputVisible) {
            auto.focus();
        }
    }, [inputVisible]);

    const showInput = () => {
        setInputVisible(true);
    };
    const handleInputChange = e => {
        setInputValue(e.target.value);
    };
    const handleClose = removedTag => {
        const tags = newTags.filter(tag => tag.key !== removedTag.key);
        setNewTags(tags);
    };

    const handleInputConfirm = () => {
        const inputValue1 = inputValue;
        let newTags1 = newTags;
        if (
            inputValue1 &&
            newTags1.filter(tag => tag.label === inputValue1).length === 0
        ) {
            newTags1 = [
                ...newTags1,
                {
                    key: `new-${newTags1.length}`,
                    label: inputValue1
                }
            ];
        }

        setNewTags([...newTags1]);
        setInputVisible(false);
        setInputValue('');
    };

    return (
        <Card
            style={{
                marginBottom: 24
            }}
            loading={true}
        >
            <div className="avatarHolder">
                <img
                    alt="avatar"
                    src="https://avatars1.githubusercontent.com/u/8186664?s=460&v=4"
                />
                <div className="name">ao nguyen</div>
                <div>
                    muốn thành công phải trải qua nhìu thất bại. Trên đường đời
                    có dại mới có khôn
                </div>
            </div>
            <div className="detail">
                <p>
                    <PhoneOutlined className="icon-title" />
                    90890867676
                </p>
                <p>
                    <MailOutlined className="icon-title" />
                    aonguyen@gmail.com
                </p>
                <p>
                    <EnvironmentOutlined className="icon-title" />
                    phu hung binh tan tay son binh dinh
                </p>
            </div>
            <Divider dashed />
            <div className="tags">
                <div className="tagsTitle">Interests</div>
                {newTags.map(item => (
                    <Tag
                        key={item.key}
                        closable
                        onClose={() => handleClose(item)}
                    >
                        {item.label}
                    </Tag>
                ))}
                {inputVisible && (
                    <Input
                        ref={ref => (auto = ref)}
                        type="text"
                        size="small"
                        style={{
                            width: 78
                        }}
                        value={inputValue}
                        onChange={handleInputChange}
                        onBlur={handleInputConfirm}
                        onPressEnter={handleInputConfirm}
                    />
                )}
                {!inputVisible && (
                    <Tag
                        onClick={showInput}
                        style={{
                            background: '#fff',
                            borderStyle: 'dashed'
                        }}
                    >
                        <PlusOutlined />
                    </Tag>
                )}
            </div>
            <Divider
                style={{
                    marginTop: 16
                }}
                dashed
            />
            <div className="team">
                <div className="teamTitle">Skill</div>
            </div>
        </Card>
    );
}
