import React from 'react';
import {
    PageHeader,
    Menu,
    Dropdown,
    Icon,
    Button,
    Tag,
    Typography,
    Row,
    Card,
    Form,
    Input,
    Upload
} from 'antd';
import './styles.css';
const { TextArea } = Input;

function Home(props) {
    const propsUpload = {
        action: 'https://www.mocky.io/v2/5cc8019d300000980a055e76',
        listType: 'picture',
        defaultFileList: [],
        className: 'upload-list-inline'
    };
    const content = (
        <div className="content">
            <Row>
                <Form>
                    <Form.Item>
                        <TextArea rows={4} placeholder="Ban dang nghi gi" />
                    </Form.Item>
                    <Form.Item>
                        <Upload {...propsUpload} listType="picture-card">
                            <div>
                                <Icon type="plus" />
                                <div className="ant-upload-text">Upload</div>
                            </div>
                        </Upload>
                    </Form.Item>
                </Form>
            </Row>
        </div>
    );

    const Content = ({ children }) => {
        return (
            <Row className="content" type="flex">
                <div className="main" style={{ flex: 1 }}>
                    {children}
                </div>
            </Row>
        );
    };

    return (
        <Card
            title="Create Post"
            size="small"
            actions={[<Icon title="post" type="edit" key="edit" />]}
            style={{margin: '0', padding: '0', border: '1px solid red'}}
        >
            <PageHeader
                title="Ao Nguyen"
                tags={<Tag color="blue">Online</Tag>}
                avatar={{
                    src:
                        'https://avatars1.githubusercontent.com/u/8186664?s=460&v=4'
                }}
                style={{ margin: '0', padding: '0', border: '1px solid blue' }}
            >
                <Content>{content}</Content>
            </PageHeader>
        </Card>
    );
}
export default Home;
