import {
    GET_CURRENT_PLACE,
    GET_ERRORS,
    GET_PROVINCES
} from './../constants/types';
import { callAPI, getAccessToken } from './../common';
import axios from 'axios';

const getCurrentPlace = (lat, lon, history) => async dispatch => {
    const result = await getAccessToken(history);
    if (result) {
        callAPI(`/weather/get-geocode?latitude=${lat}&longitude=${lon}`)
            .then(res => {
                dispatch({
                    type: GET_CURRENT_PLACE,
                    payload: res.data
                });
            })
            .catch(err => {
                dispatch({
                    type: GET_ERRORS,
                    payload: err.response.data
                });
            });
    }
};
const getProvinces = () => async dispatch => {
    try {
        axios
            .get(
                'https://dc.tintoc.net/app/api-customer/public/provinces?size=65'
            )
            .then(res => {
                dispatch({
                    type: GET_PROVINCES,
                    payload: res.data
                });
            })
            .catch(err => {
                dispatch({
                    type: GET_ERRORS,
                    payload: err.response.data
                });
            });
    }
    catch(e)
    {
        console.log(e)
    }
};

export default {
    getCurrentPlace,
    getProvinces
};
