import React, { forwardRef, useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Spin, Select, Row, Col } from 'antd';
import allActions from './../../../../actions';
import './styles.css';
const { Option } = Select;
const nullSelectItem = {
    label: '',
    key: ''
};
// const province = [
//     {
//         id: 1,
//         name: 'Binh dinh'
//     },
//     {
//         id: 2,
//         name: 'Son la'
//     }
// ]
// const city = [
//     {
//         id: 1,
//         name: 'Quy nhon'
//     },
//     {
//         id: 2,
//         name: 'Tp Son la'
//     }
// ]
const GeographicView = forwardRef((props, ref) => {
    const [loading, setLoading] = useState(false);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(allActions.geocodeActions.getProvinces());
    }, []);
    const provinces = useSelector(state => state.provincesReducer.provinces);

    const getProvinceOption = () => {
        if (provinces) {
            return getOption(provinces);
        }
        return [];
    };
    // const getCityOption = () => {
    //     // const { city } = props;
    //     if (city) {
    //         return getOption(city);
    //     }
    //     return [];
    // };
    const getOption = list => {
        if (!list || list.length < 1) {
            return (
                <Option key={0} value={0}>
                    没有找到选项
                </Option>
            );
        }
        return list.map(item => (
            <Option key={item.ID} value={item.ID}>
                {item.Title}
            </Option>
        ));
    };
    const selectProvinceItem = item => {
        
        const { dispatch, onChange } = props;

        // if (dispatch) {
        //     dispatch({
        //         type: 'accountAndsettings/fetchCity',
        //         payload: item.key
        //     });
        // }

        // if (onChange) {
        //     onChange({
        //         province: item,
        //         city: nullSelectItem
        //     });
        // }
    };
    const selectCityItem = item => {
        const { value, onChange } = props;

        // if (value && onChange) {
        //     onChange({
        //         province: value.province,
        //         city: item
        //     });
        // }
    };

    return (
        <Row>
            <Col xl={24} lg={24} md={24} sm={24} xs={24}>
                <Spin spinning={loading} wrapperClassName="row" size="small">
                    <Select
                        className="item"
                        value="province"
                        showSearch
                        onSelect={selectProvinceItem}
                    >
                        {getProvinceOption()}
                    </Select>
                    {/* <Select
                        className="item"
                        value="city"
                        showSearch
                        onSelect={selectCityItem}
                    >
                        {getCityOption()}
                    </Select> */}
                </Spin>
            </Col>
        </Row>
    );
});

export default GeographicView;
