import React, { forwardRef } from 'react';
import { Spin, Select } from 'antd';
import './styles.css';
const { Option } = Select;
const nullSelectItem = {
    label: '',
    key: ''
};

const GeographicView = forwardRef((props, ref) => {
    const getProvinceOption = () => {
        // const { province } = this.props;
        if (province) {
            return getOption(province);
        }
        return [];
    };
    const getCityOption = () => {
        // const { city } = this.props;
        if (city) {
            return getOption(city);
        }
        return [];
    };
    const getOption = list => {
        if (!list || list.length < 1) {
            return (
                <Option key={0} value={0}>
                    没有找到选项
                </Option>
            );
        }
        return list.map(item => (
            <Option key={item.id} value={item.id}>
                {item.name}
            </Option>
        ));
    };
    const selectProvinceItem = item => {
        const { dispatch, onChange } = props;

        if (dispatch) {
            dispatch({
                type: 'accountAndsettings/fetchCity',
                payload: item.key
            });
        }

        if (onChange) {
            onChange({
                province: item,
                city: nullSelectItem
            });
        }
    };
    const selectCityItem = item => {
        const { value, onChange } = props;

        if (value && onChange) {
            onChange({
                province: value.province,
                city: item
            });
        }
    };

    return (
        <Spin spinning={loading} wrapperClassName='row'>
            <Select
                className='item'
                value={province}
                labelInValue
                showSearch
                onSelect={selectProvinceItem}
            >
                {getProvinceOption()}
            </Select>
            <Select
                className='item'
                value={city}
                labelInValue
                showSearch
                onSelect={selectCityItem}
            >
                {getCityOption()}
            </Select>
        </Spin>
    );
});

export default GeographicView;
