import React from 'react';
import { List, Tag } from 'antd';
import { StarTwoTone, LikeOutlined, MessageTwoTone } from '@ant-design/icons';
import './styles.css';
import mock from './mock';
import PostListContent from './../PostListContent/PostListContent';

export default function ListPosts() {

    const IconText = ({ icon, text }) => (
        <span>
            {icon} {text}
        </span>
    );

    return (
        <List
            size="large"
            className='articleList'
            rowKey="id"
            itemLayout="vertical"
            dataSource={mock}
            renderItem={item => (
                <List.Item
                    key={item.id}
                    actions={[
                        <IconText
                            key="star"
                            icon={<StarTwoTone />}
                            text={item.star}
                        />,
                        <IconText
                            key="like"
                            icon={<LikeOutlined />}
                            text={item.like}
                        />,
                        <IconText
                            key="message"
                            icon={<MessageTwoTone />}
                            text={item.message}
                        />
                    ]}
                >
                    <List.Item.Meta
                        title={
                            <a
                                className={'listItemMetaTitle'}
                                href={item.href}
                            >
                                {item.title}
                            </a>
                        }
                        description={
                            <span>
                                <Tag>Ant Design</Tag>
                                <Tag>设计语言</Tag>
                                <Tag>蚂蚁金服</Tag>
                            </span>
                        }
                    />
                    <PostListContent data={item} />
                </List.Item>
            )}
        />
    );
}
