import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Divider, Tag, Input, Avatar } from 'antd';
import { Link } from 'react-router-dom';
import { PlusOutlined } from '@ant-design/icons';
import { GridContent } from '@ant-design/pro-layout';
import './styles.css';

export default function Account(props) {
    const [inputVisible, setInputVisible] = useState(false);
    const [inputValue, setInputValue] = useState('');
    const [newTags, setNewTags] = useState([]);
    let auto = undefined;

    useEffect(() => {
        if (inputVisible) {
            auto.focus();
        }
    }, [inputVisible]);

    const showInput = () => {
        setInputVisible(true);
    };
    const handleInputChange = e => {
        setInputValue(e.target.value);
    };
    const handleClose = removedTag => {
        const tags = newTags.filter(tag => tag.key !== removedTag.key);
        setNewTags(tags);
    };

    const handleInputConfirm = () => {
        const inputValue1 = inputValue;
        let newTags1 = newTags;
        if (
            inputValue1 &&
            newTags1.filter(tag => tag.label === inputValue1).length === 0
        ) {
            newTags1 = [
                ...newTags1,
                {
                    key: `new-${newTags1.length}`,
                    label: inputValue1
                }
            ];
        }

        setNewTags([...newTags1]);
        setInputVisible(false);
        setInputValue('');
    };  

    return (
        <GridContent>
            <Row gutter={[16, 16]}>
                <Col xl={7} lg={7} md={7} sm={24} xs={24}>
                    <Card
                        style={{
                            marginBottom: 24
                        }}
                        //loading={dataLoading}
                    >
                        <div className="avatarHolder">
                            <img
                                alt="avatar"
                                src="https://avatars1.githubusercontent.com/u/8186664?s=460&v=4"
                            />
                            <div className="name">ao nguyen</div>
                            <div>aonguyen@gmail.com</div>
                        </div>
                        <div className="detail">
                            <p>
                                <i className="title"><PlusOutlined /></i>
                                title
                            </p>
                            <p>
                                <i className="group" />
                                group
                            </p>
                            <p>
                                <i className="address" />
                                phu hung binh tan tay son binh dinh
                            </p>
                        </div>
                        <Divider dashed />
                        <div className="tags">
                            <div className="tagsTitle">标签</div>
                            {newTags.map(item => (
                                <Tag
                                    key={item.key}
                                    closable
                                    onClose={() => handleClose(item)}
                                >
                                    {item.label}
                                </Tag>
                            ))}
                            {inputVisible && (
                                <Input
                                    ref={ref => (auto = ref)}
                                    type="text"
                                    size="small"
                                    style={{
                                        width: 78
                                    }}
                                    value={inputValue}
                                    onChange={handleInputChange}
                                    onBlur={handleInputConfirm}
                                    onPressEnter={handleInputConfirm}
                                />
                            )}
                            {!inputVisible && (
                                <Tag
                                    onClick={showInput}
                                    style={{
                                        background: '#fff',
                                        borderStyle: 'dashed'
                                    }}
                                >
                                    <PlusOutlined />
                                </Tag>
                            )}
                        </div>
                        <Divider
                            style={{
                                marginTop: 16
                            }}
                            dashed
                        />
                        <div className="team">
                            <div className="teamTitle">团队</div>
                            <Row gutter={36}>
                                <Col lg={24} xl={12}>
                                    <Link to="foo">
                                        <Avatar
                                            size="small"
                                            src="https://avatars1.githubusercontent.com/u/8186664?s=460&v=4"
                                        />
                                        ant design
                                    </Link>
                                </Col>
                            </Row>
                        </div>
                    </Card>
                </Col>
                <Col xl={17} lg={17} md={17} sm={24} xs={24}>
                    <Card>account</Card>
                </Col>
            </Row>
        </GridContent>
    );
}
