import React, { useState, useEffect } from 'react';
import { GridContent } from '@ant-design/pro-layout';
import { Menu } from 'antd';
import './styles.css';

const { Item } = Menu;
const menuMap = {
    base: 'Basic Settings',
    security: 'Security Settings',
    notification: 'Message Notification'
};

export default function Setting() {
    const [selectKey, setSelectKey] = useState('base');
    const [mode, setMode] = useState('inline');
    let main = undefined;

    const getMenu = () => {
        return Object.keys(menuMap).map(item => (
            <Item key={item}>{menuMap[item]}</Item>
        ));
    };
    const getRightTitle = () => {
        return menuMap[selectKey];
    };
    const renderChildren = () => {
        switch (selectKey) {
            case 'base':
                return <h1>base</h1>;

            case 'security':
                return <h1>security</h1>;

            case 'notification':
                return <h1>notification</h1>;

            default:
                break;
        }
        return null;
    };
    const selectedKey = key => {
        setSelectKey(key);
    };
    const resize = () => {
        if (!main) {
            return;
        }
        requestAnimationFrame(() => {
            if (!main) {
                return;
            }
            let mode = 'inline';
            const { offsetWidth } = main;

            if (main.offsetWidth < 641 && offsetWidth > 400) {
                mode = 'horizontal';
            }

            if (window.innerWidth < 768 && offsetWidth > 400) {
                mode = 'horizontal';
            }
            setMode(mode);
        });
    };
    useEffect(() => {
        window.addEventListener('resize', resize);
        

        return () => {
            window.removeEventListener('resize', resize);
        }
    }, [])

    return (
        <GridContent>
            <div
                className="main"
                ref={ref => {
                    if (ref) {
                        main = ref;
                    }
                }}
            >
                <div className="leftMenu-account">
                    <Menu
                        mode={mode}
                        selectedKeys={[selectKey]}
                        onClick={({ key }) => selectedKey(key)}
                    >
                        {getMenu()}
                    </Menu>
                </div>
                <div className="right">
                    <div className="title">{getRightTitle()}</div>
                    {renderChildren()}
                </div>
            </div>
        </GridContent>
    );
}
