import React, { useState } from 'react';
import { List, message, Avatar, Spin, Card, Icon } from 'antd';
import AvatarList from 'ant-design-pro/lib/AvatarList';
import axios from 'axios';
import { BASE_URI } from './../../../../constants/base_url';

import InfiniteScroll from 'react-infinite-scroller';
import './styles.css';

export default function ManyComponent() {
    const [listData, setListData] = useState([]);
    const [hasMoreItems, setHasMoreItems] = useState(true);
    const [loading, setLoading] = useState(false);

    const IconText = ({ type, text }) => (
        <span>
            <Icon type={type} style={{ marginRight: 8 }} />
            {text}
        </span>
    );

    const loadItems = page => {
        // axios
        //     .get(BASE_URI + '/getAllData', {
        //         params: {
        //             page,
        //             page_size: 20
        //         }
        //     })
        //     .then(res => {
        //         const items = res.data;
        //         setTracks([...tracks, ...items.users]);
        //     })
        //     .catch(err => {
        //         setHasMoreItems(false);
        //     });
        const data = [
            {
                href: 'http://ant.design',
                title: `ant design part 1`,
                avatar:
                    'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
                content:
                    'We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.'
            }
        ];
        setLoading(true);
        if (listData.length === 50) {
            message.warning('Infinite List loaded all');
            setHasMoreItems(false);
            setLoading(false);
            return;
        }
        setTimeout(setListData([...listData, ...data]), 5000);
    };

    return (
        <div className="list-content">
            <InfiniteScroll
                pageStart={0}
                loadMore={loadItems}
                hasMore={hasMoreItems}
            >
                <List
                    itemLayout="vertical"
                    size="small"
                    dataSource={listData}
                    renderItem={item => (
                        <List.Item
                            key={item.title}
                            actions={[
                                <IconText
                                    type="like-o"
                                    text="156"
                                    key="list-vertical-like-o"
                                />,
                                <IconText
                                    type="dislike-o"
                                    text="156"
                                    key="list-vertical-like-o"
                                />,
                                <IconText
                                    type="message"
                                    text="2"
                                    key="list-vertical-message"
                                />
                            ]}
                            extra={
                                <img
                                    width={272}
                                    alt="logo"
                                    src="https://gw.alipayobjects.com/zos/rmsportal/mqaQswcyDLcXyDKnZfES.png"
                                />
                            }
                        >
                            <List.Item.Meta
                                avatar={<Avatar src={item.avatar} />}
                                title={<a href={item.href}>{item.title}</a>}
                            />
                            {item.content}
                            <AvatarList size="mini">
                                <AvatarList.Item
                                    tips="Jake"
                                    src="https://gw.alipayobjects.com/zos/rmsportal/zOsKZmFRdUtvpqCImOVY.png"
                                />
                                <AvatarList.Item
                                    tips="Andy"
                                    src="https://gw.alipayobjects.com/zos/rmsportal/sfjbOqnsXXJgNCjCzDBL.png"
                                />
                                <AvatarList.Item
                                    tips="Niko"
                                    src="https://gw.alipayobjects.com/zos/rmsportal/kZzEzemZyKLKFsojXItE.png"
                                />
                            </AvatarList>
                        </List.Item>
                    )}
                >
                    {hasMoreItems && (
                        <div className="loading-content">
                            <Spin />
                        </div>
                    )}
                </List>
            </InfiniteScroll>
        </div>
    );
}

